// This is sets the requirements for the database, allowing connection through the knex node.js package to connect
// to it's parameters and showing data in our website
const knex = require('knex')({
  client: 'postgresql',
  connection: {
    host: process.env.RDS_HOSTNAME || 'localhost',
    user: process.env.RDS_USERNAME || 'postgres',
    password: process.env.RDS_PASSWORD || 'clatour0',
    database: 'ellarises',
    port: process.env.RDS_PORT || 5432,
    ssl: process.env.RDS_HOSTNAME ? { rejectUnauthorized: false } : false
  },
  pool: {
    min: 2,
    max: 10
  }
});

module.exports = knex;
